def find_floor(instructions):
    floor = 0

    for instruction in instructions:
        if instruction == "(":
            floor += 1
        elif instruction == ")":
            floor -= 1

    return floor


def find_basement(instructions):
    floor = 0

    for position, instruction in enumerate(instructions, start=1):
        if instruction == "(":
            floor += 1
        elif instruction == ")":
            floor -= 1

        # check if santa enters the basement
        if floor == -1:
            return position

    return -1


